import express from 'express';
import createError from 'http-errors';
import CookieParser from 'cookie-parser';
import { CFLogger, CFLogLevel } from '@yj-intl/node-cf-logger';
import config from './config/config';
import route from './routes/route';

/**
 * this is express logic for application
 * @type {*|Express|Function}
 */
const app = express();

/** ADD SETTING **/
app.use(express.urlencoded({ extended: false }));
app.use(express.json({ limit: '10mb' }));
app.use(CookieParser());

/** ERROR HANDLING **/
if (process.env.NODE_ENV === 'development') {
  process.on('unhandledRejection', (err, p) => {
    CFLogger.log(CFLogLevel.CRITICAL, `Error : ${err}`);
    CFLogger.log(CFLogLevel.CRITICAL, `Promise : ${p}`);
  });
}

process.on('unhandledRejection', (err, p) => {
  //  CFLogger.log(CFLogLevel.CRITICAL, `Error : ${err}`);
  //  CFLogger.log(CFLogLevel.CRITICAL, `Promise : ${p}`);
});

process.on('uncaughtException', err => {
  CFLogger.log(CFLogLevel.CRITICAL, `uncaughtException : ${err}`);
  process.exit(config.errors.number.processExit);
});

/** ROUTES **/
app.use(route);

// catch 404 and forward to error handler
app.use((req, res, next) => {
  next(createError(config.errors.status.notFound));
});

module.exports = app;

/**
 * object variable for config
 * @type {{port: {app: number, jsonServer: number}, errors: {status: {ok: number, internalServerError: number, notFound: number}, number: {processExit: number}, errorCode: {permissionDenied: string, addressInUsed: string}}}}
 */
const config = {
  port: {
    app: 8000,
    jsonServer: 3003
  },
  errors: {
    status: {
      ok: 200,
      internalServerError: 500,
      notFound: 404
    },
    number: {
      processExit: 1
    },
    errorCode: {
      permissionDenied: "EACCES",
      addressInUsed: "EADDRINUSE"
    }
  }
};

export default config;

import Mail from "./../models/Mail";
import config from "./../config/config";

/**
 * this is Controller class
 */
export default class Controller {
  /**
   * constructor
   */
  constructor() {
    /**
     * create a instance of Controller
     * @type {Mail} mail
     * @private
     */
    this.mail = new Mail();
    /**
     * create values Object
     * @type {Object} values
     * @private
     */
    this.values = {};
  }

  /**
   * this is fetchMailList method
   * call fetchRegisteredMailList from Mail model
   * @param {Object} req - request Object
   * @param {Object} res - response
   * @returns {Promise<*>}
   */
  async fetchMailList(req, res) {
    this.values.id = req.query.id ? req.query.id : null;
    const result = await this.mail.fetchRegisteredMailList(this.values);
    const statusCode = !result.err
      ? config.errors.status.ok
      : config.errors.status.internalServerError;
    return res.status(statusCode).json(JSON.parse(result.text));
  }
}

